console.log(typeof 91); // typeof number
console.log(typeof false); // typeof boolean
console.log(typeof "245"); // typeof string
console.log(typeof undefined); // typeof undefined
console.log(typeof null); // typeof object

//! Primitive Data Types

//* Primitive data types in JavaScript include:

//? Numbers - Integers, floats
//? Strings - Any data under single quote, double quote or backtick quote
//? Booleans - true or false value
//? Null - empty value or no value
//? Undefined - a declared variable without a value
//? Symbol - A unique value that can be generated by Symbol constructor

let word = "Javascript";
console.log(word); // Javascript

console.log(word[0]); // J

let numOne = 3;
let numTwo = 3;

console.log(numOne == numTwo); // true

let js = "Javascript";
let py = "Python";

console.log(js == py); // false

let lightOn = true;
let lightOff = false;

console.log(lightOn == lightOff); // false

//! Non-Primitive Data Types

//* Non-primitive data types in JavaScript includes:

//? Objects
//? Arrays

let nums = [1, "prototurk", 3]; //! Array

console.log(nums); // [1, 'prototurk', 3]

nums[1] = 2;

console.log(nums); // [1, 2, 3]

let nums1 = [1, 2, 3];
let nums2 = [1, 2, 3];

console.log(nums1 == nums2); // false

let userOne = {
  name: "Asabeneh",
  role: "teaching",
  country: "Finland",
};

let userTwo = {
  name: "Asabeneh",
  role: "teaching",
  country: "Finland",
};

console.log(userOne == userTwo); // false

let user1 = {
  name: "Tayfun",
  surname: "Erbilen",
};

let user2 = {
  name: "Tayfun",
  surname: "Erbilen",
};

console.log(user1 == user2); // false

let nums0 = [1, 2, 3];
let numbers = nums0;

console.log(nums0 == numbers); // true

let userOne1 = {
  name: "Asabeneh",
  role: "teaching",
  country: "Finland",
};

let userTwo2 = userOne1;

